var LocalStorageHandler=function(){var b=window.localStorage;this.length=b.length;this.get=function(a){try{return JSON.parse(b.getItem(a))}catch(c){return b.getItem(a)}};this.set=function(a,c){b.setItem(a,JSON.stringify(c));return this.get(a)};this.key=function(a){if("number"===typeof a)return b.key(a)};this.data=function(){for(var a=0,c=[];b.key(a);)c[a]=[b.key(a),this.get(b.key(a))],a++;return c.length?c:null};this.remove=function(a){var c=!1;a="number"===typeof a?this.key(a):a;a in b&&(c=!0,b.removeItem(a));
return c};this.clear=function(){var a=b.length;b.clear();return a}};
